import react, {useState} from "react";
import axios from 'axios';



function App() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&lang=ru&units=metric&appid=bf954ddecfe7ef1ae36bc69f026157c0`
  const searchLocation = (event) => {
    if (event.key === 'Enter') {
    axios.get(url).then((response) => {
      setData(response.data)
      console.log(response.data);
    })
    setLocation('');
  }
  }


  return (
    <div className="app">
      <div className="search">
        <input 
          value={location}
          onChange={event => setLocation(event.target.value)}
          placeholder="Enter location"
          onKeyPress={searchLocation}
          type="text"/>
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
          </div>
          <div className="temp">
            {data.main ? <h1>{data.main.temp.toFixed()}°С</h1> : null}
          </div>
          <div className="description">
            {data.weather ? <img src={`https://openweathermap.org/img/w/${data.weather[0].icon}.png`}></img> : null}
            {/* `https://openweathermap.org/img/w/${newfilteredWeather[0].weather[0].icon}.png` */}
          </div>
        </div>
        <div className="bottom">
          <div className="feels">
            <p className="bold">20°C</p>
            <p>Feels Like</p>
          </div>
          <div className="hunidity">
            <p className="bold">20°C</p>
            <p>Hunidity</p>
          </div>
          <div className="wind">
            <p className="bold">20km/h</p>
            <p>Wind speed</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
